    INSERT INTO artwork (slug, artwork_name, artist_slug, price, size_in, size_cm, description, additional_information, material, medium, image)    VALUES ('andy-warhol-van-heusen-ronald-reagan-from-the-ads-series', 'van heusen ronald reagan from the ads series', 'andy-warhol', '1000000.0', '38 × 38 in', '96.5 × 96.5 cm', 'None', '<p>Andy Warhol Van Heusen, 1985 from Ads Series features an advertisement for the popular men’s shirt brand, Van Heusen</p>', '''Includes etchings; engravings; lithographs; monoprints; screen prints; woodcuts.''', '''Screenprint in colors on Lenox Museum Board''', 'https://d7hftxdivxxvm.cloudfront.net?height=800&quality=80&resize_to=fit&src=https%3A%2F%2Fd32dm0rphc51dk.cloudfront.net%2F7Q07GoyhdQKwd77YtsLSsg%2Fnormalized.jpg&width=800')    
